Bash(NODE_ENV=production DATABASE_URL="postgresql://postgres.jqqfjfoigtwdpnlicjmh:Lgr5ovbpji64CooD@aws-0-eu-north-1.pooler.supabase.com:6543/postgres" node -e "
const { sequelize } = require(''./backend/src/database/connection.js'');
(async () => {
  try {
    console.log(''🧪 Testing direct database insert...'');

    // Test direct SQL insert
    const testData = {
      session_id: ''test_session_'' + Date.now(),
      store_id: ''157d4590-49bf-4b0b-bd77-abe131909528'',
      activity_type: ''test_tracking'',
      page_url: ''http://localhost:3000/test'',
      referrer: '''',
      user_agent: ''Test Script'',
      metadata: JSON.stringify({ test: true, timestamp: new Date().toISOString() })
    };

    const [result] = await sequelize.query(
      ''INSERT INTO customer_activities (session_id, store_id, activity_type, page_url, referrer, user_agent, metadata, created_at, updated_at) VALUES (:session_id, :store_id, :activity_type, :page_url, :referrer, :user_agent, CAST(:metadata AS JSON), NOW(), NOW()) RETURNING id'',
      {
        replacements: testData,
        type: sequelize.QueryTypes.INSERT
      }
    );

    console.log(''✅ Test record inserted with ID:'', result[0].id);

