    const [rawResults] = await sequelize.query(''SELECT config_data FROM integration_configs WHERE store_id = :storeId AND integration_type = :type AND is_active = true'', {
      replacements: { storeId, type: ''akeneo'' }
    });

    const rawConfig = rawResults[0].config_data;
    const key = process.env.INTEGRATION_ENCRYPTION_KEY || ''catalyst-integration-default-key-change-in-production'';

    // First decryption
    const encryptedValue1 = rawConfig.clientSecret.replace(''encrypted:'', '''');
    const decipher1 = crypto.createDecipher(''aes-256-cbc'', key);
    let decryptedValue1 = decipher1.update(encryptedValue1, ''hex'', ''utf8'');
    decryptedValue1 += decipher1.final(''utf8'');

    console.log(''📋 After first decryption:'');
    console.log(''  Length:'', decryptedValue1.length);
    console.log(''  Value:'', decryptedValue1.substring(0, 30) + ''...'');

    // Second decryption (if still encrypted)
    if (decryptedValue1.startsWith(''encrypted:'')) {
      console.log(''🔄 Performing second decryption...'');
      const encryptedValue2 = decryptedValue1.replace(''encrypted:'', '''');
      const decipher2 = crypto.createDecipher(''aes-256-cbc'', key);
      let decryptedValue2 = decipher2.update(encryptedValue2, ''hex'', ''utf8'');
      decryptedValue2 += decipher2.final(''utf8'');

      console.log(''📋 After second decryption:'');
      console.log(''  Length:'', decryptedValue2.length);
      console.log(''  Value (first 20 chars):'', decryptedValue2.substring(0, 20) + ''...'');
      console.log(''  Value (last 20 chars):'', ''...'' + decryptedValue2.substring(decryptedValue2.length - 20));

      // Test if this looks like a valid client secret
      const isValidSecret = decryptedValue2.length > 50 && !decryptedValue2.startsWith(''encrypted:'');
      if (isValidSecret) {
        console.log(''✅ This looks like the actual client secret!'');
      }
    }

    await sequelize.close();
  } catch (error) {
    console.error(''❌ Error:'', error.message);
  }
})();")

