const { sequelize } = require(''./backend/src/database/connection.js'');

(async () => {
  try {
    console.log(''üîß Adding Supabase to integration type enum...'');

    await sequelize.query(''ALTER TYPE enum_integration_configs_integration_type ADD VALUE IF NOT EXISTS \''supabase\'';'');
    console.log(''‚úÖ Supabase added to integration type enum'');

    console.log(''üîß Creating supabase_oauth_tokens table...'');

    await sequelize.query(\`
      CREATE TABLE IF NOT EXISTS supabase_oauth_tokens (
          id UUID DEFAULT gen_random_uuid() PRIMARY KEY,
          store_id UUID NOT NULL REFERENCES stores(id) ON DELETE CASCADE,
          access_token TEXT NOT NULL,
          refresh_token TEXT NOT NULL,
          expires_at TIMESTAMP NOT NULL,
          project_url TEXT NOT NULL,
          anon_key TEXT NOT NULL,
          service_role_key TEXT,
          database_url TEXT,
          storage_url TEXT,
          auth_url TEXT,
          created_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
          updated_at TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
          UNIQUE(store_id)
      );
    \`);

    console.log(''‚úÖ supabase_oauth_tokens table created'');

    console.log(''üîß Creating index...'');
    await sequelize.query(''CREATE INDEX IF NOT EXISTS idx_supabase_oauth_tokens_store_id ON supabase_oauth_tokens(store_id);'');
    console.log(''‚úÖ Index created'');

    console.log(''üîß Creating trigger function...'');
    await sequelize.query(\`
      CREATE OR REPLACE FUNCTION update_updated_at_column()
      RETURNS TRIGGER AS $$
      BEGIN
          NEW.updated_at = CURRENT_TIMESTAMP;
          RETURN NEW;
      END;
      $$ language ''plpgsql'';
    \`);

    await sequelize.query(\`
      DROP TRIGGER IF EXISTS update_supabase_oauth_tokens_updated_at ON supabase_oauth_tokens;
      CREATE TRIGGER update_supabase_oauth_tokens_updated_at
          BEFORE UPDATE ON supabase_oauth_tokens
          FOR EACH ROW
          EXECUTE FUNCTION update_updated_at_column();
    \`);
    console.log(''‚úÖ Trigger created'');

    await sequelize.close();
    console.log(''üéâ Supabase integration migration completed successfully!'');
  } catch (error) {
    console.error(''‚ùå Migration failed:'', error.message);
    await sequelize.close();
  }
})();
")

