    });

    const rawConfig = rawResults[0].config_data;
    const key = process.env.INTEGRATION_ENCRYPTION_KEY || ''catalyst-integration-default-key-change-in-production'';

    // First decryption
    const encryptedValue1 = rawConfig.clientSecret.replace(''encrypted:'', '''');
    const decipher1 = crypto.createDecipher(''aes-256-cbc'', key);
    let decryptedValue1 = decipher1.update(encryptedValue1, ''hex'', ''utf8'');
