const { createClient } = require(''@supabase/supabase-js'');
const supabaseIntegration = require(''./backend/src/services/supabase-integration'');

const STORE_ID = ''157d4590-49bf-4b0b-bd77-abe131909528'';

(async () => {
  try {
    // Get token info
    const tokenInfo = await supabaseIntegration.getTokenInfo(STORE_ID);

    if (!tokenInfo || !tokenInfo.anon_key) {
      console.log(''‚ùå No Supabase credentials found'');
      return;
    }

    console.log(''‚úÖ Creating Supabase client...'');
    console.log(''   Project URL:'', tokenInfo.project_url);

    // Create Supabase client
    const supabase = createClient(tokenInfo.project_url, tokenInfo.anon_key);

    console.log(''\nüì¶ Fetching buckets...'');
    const { data: buckets, error } = await supabase.storage.listBuckets();

    if (error) {
      console.log(''‚ùå Error fetching buckets:'', error.message);
    } else {
      console.log(''‚úÖ Successfully fetched buckets:\n'');

      if (!buckets || buckets.length === 0) {
        console.log(''   No buckets found in this project'');
      } else {
        buckets.forEach((bucket, index) => {
          console.log(''   '' + (index + 1) + ''. '' + bucket.name);
          console.log(''      - ID: '' + bucket.id);
          console.log(''      - Public: '' + (bucket.public ? ''Yes'' : ''No''));
          console.log(''      - Created: '' + bucket.created_at);
          console.log('''');
        });
      }
    }
  } catch (error) {
    console.error(''Error:'', error.message);
  }
})();
")

